import React, { Component } from 'react';
import './App.css';

import Game from './Game';
import Board from './Bo'

const game = new Game(4);

class App extends Component {

  constructor() {
    super();
    this.state = { game };
  }

  setCallback() {
    game.onMove(updatedGame => this.setState({game: updatedGame}));
    this.setState({ game });
  }

  render() {

    game && console.log(game.convertTo2D(game.gameState.board))

    return (
      <div className="App">

        <h3 className="header">2048 for COMP 426</h3>

        <Board game={game} />

      </div>
    );
  }
}

export default App;
