import React, { Component } from 'react';
import './App.css';

import Game from './Game';

const game = new Game(4);

class App extends Component {

  constructor() {
    super();
    this.state = { game };
  }

  setCallback() {
    game.onMove(updatedGame => this.setState({game: updatedGame}));
    this.setState({ game });
  }

  render() {

    game && console.log(game.convertTo2D(game.gameState.board))

    return (
      <div className="App">

        {game && <div className="game-board">

          {game.gameState.board.map((number, i) => (
             <div key={i}>{number}</div>
          ))}

        </div>}

      </div>
    );
  }
}

export default App;
