class Game {

    constructor(dimension) {
        this.dimension = dimension;
        this.setupNewGame()
    }

    setupNewGame() {
        board  = this.blankGrid();
        addNumber(board);
        addNumber(board);
    }

    addNumber(board) {
        const openings = [], { dimension } = this;

        for (let i = 0; i < dimension; i++) {
            for (let j = 0; j < dimension; j++) {
                if (board[i][j] === 0) 
                    openings.push({col: i, row: j});

            }
        }

        if(openings.length === 0)
            return;

        if (options.length > 0) {
            let spot = random(options);
            let r = random(1);
            grid[spot.x][spot.y] = r > 0.1 ? 2 : 4;
        }
    }

    getRandomNumber = () => Math.random() > .1 ? 2 : 4;
    getRandomArrayIndex = arr => Math.floor(arr.length * Math.random())

    blankGrid() {
        const grid = [], { dimension } = this;
        for(let i = 0; i < dimension; i++) {
            grid.push(Array(dimension).fill(0));
        }
        return grid;
    }
}

export default Game;
