import React, { Component } from 'react';
import './App.css';

import Game from './Game';

const game = new Game(4);

class App extends Component {

  constructor() {
    super();
    this.state = { game };
  }

  setCallback() {
    game.onMove(updatedGame => this.setState({game: updatedGame}));
    this.setState({ game });
  }

  render() {
    console.log(game)
    return (
      <div className="App">

        <div className="game-board">

        </div>

      </div>
    );
  }
}

export default App;
