class Game {

    constructor(dimension) {
        this.gameState = {
            board: this.blankGrid(dimension),
            score: 0,
            won: false,
            over: false
        }
    }

    blankGrid(dimension) {
        const grid = [];
        for(let i = 0; i < dimension; i++) {
            grid.push(Array(dimension).fill(0));
        }
        return grid;
    }
}

export default Game;
