class Game {

    constructor(dimension) {
        this.dimension = dimension;
        this.gameState = {
            board: this.setupNewGame(),
            score: 0,
            won: false,
            over: false
        }
    }

    setupNewGame() {
        const { dimension, gameState } = this;
        gameState.board = this.blankGrid();
        add
    }

    blankGrid() {
        const grid = [], { dimension } = this;
        for(let i = 0; i < dimension; i++) {
            grid.push(Array(dimension).fill(0));
        }
        return grid;
    }
}

export default Game;
